define('App/Directives/Breadcrumb',['angular', 'underscore'], function(angular, _) {
    
    var breadcrumbDirective,
        breadcrumbMap,
        createBreadcrumb,
        templateHtml;

    breadcrumbMap = {
        'home': { name: 'My Dashboard', url: '/' },
        'viewables': { name: 'Views', url: '/views' },
        'mybca': { name: 'My BCA', url: '/' },
        'favourites': { name: 'Favourites', url: '/views' },
        'themes': { name: 'Themes', url: '/themes' },
        'trades': { name: 'Trades and Allocations', url: '#/trades' },
        'research': { },
        'alert-settings': { name: 'Alert Settings', url: '/alerts/settings' },
        'alert-history': { name: 'Alert History', url: '/alerts/history' }
    };

    createBreadcrumb = function (path, $location) {
        var results = _.map(path, function(item) {
            return {
                name: breadcrumbMap[item].name,
                link: breadcrumbMap[item].url,
                click: function () {
                    $location.url(breadcrumbMap[item].url);
                }
            };
        });

        var lastItem = _.last(results);
        lastItem.link = null;
        lastItem.style = '';

        return results;
    };

    templateHtml = '' +
        '<ul class="breadcrumb" ng-show="breadcrumbs.length > 0">' +
        ' <li ng-repeat="breadcrumb in breadcrumbs">' +
        '  <span ng-hide="breadcrumb.link" ng-bind-template="{{breadcrumb.name || page.getTitle()}}"></span>' +
        '  <a ng-show="breadcrumb.link" ng-click="breadcrumb.click()" href="" data-tracking-action="breadcrumb">{{breadcrumb.name}}</a>' +
        '  <span ng-show="breadcrumb.link" class="divider">/</span>' +
        ' </li>' +
        '</ul>';

    breadcrumbDirective = function($location, Page) {
        return {
            restrict: 'EA',
            template: templateHtml,
            link: function (scope, element) {
                scope.page = Page;
                scope.$on('$routeChangeSuccess', function (event, current, last) {
                    if (current.action) {
                        var renderPath = current.action.split('.');
                        if (renderPath.length > 1 && renderPath[1] !== 'default' && breadcrumbMap.hasOwnProperty(renderPath[1])) {
                            scope.breadcrumbs = createBreadcrumb(renderPath, $location);
                        } else {
                            scope.breadcrumbs = [];
                        }
                    }
                });
            }
        };
    };

    breadcrumbDirective.$inject = ['$location', 'Page'];
    return breadcrumbDirective;
});


