        '</span>',
        '<span class="down">' +
            '<i icon state="downState" on-icon="icon-thumbs-down icon-on" off-icon="icon-thumbs-down icon-off" title="{{downTitle}}">{{sum(dislikeCount, countChange.dislike)}}</i>' +
        '</span>'
