define('App/Controllers/Alerts/AlertSettingController',['angular', 'underscore'], function (angular, _) {
    

    var AlertSettingController = function ($scope, AlertSettings, Notifications, Analytics) {

        $scope.$watch('userSetting.delivery', function (delivery) {
            $scope.frequencies = AlertSettings.getFrequencies(delivery);

            if (delivery) {
                var enabledFrequencies = _.where($scope.frequencies, { 'enabled': true });

                if (enabledFrequencies.length && !_.some(enabledFrequencies, function (f) { return f.value === $scope.userSetting.frequency; })) {
                    $scope.userSetting.frequency = enabledFrequencies[0].value;
                }
            }
        });

        $scope.$watch('userSetting', function (newVal, oldVal) {
            if (newVal && oldVal && (newVal.isEnabled !== oldVal.isEnabled ||
                    newVal.delivery !== oldVal.delivery || newVal.frequency !== oldVal.frequency)) {
                AlertSettings.putUserSetting(newVal).then(function () {
                    Notifications.success('Your settings have been saved.');
                });

                if (newVal.isEnabled !== oldVal.isEnabled) {
                    Analytics.registerClick('alerting_' + newVal.key + (newVal.isEnabled ? 'On' : 'Off'));
                }
            }
        }, true);

    };
    AlertSettingController.$inject = ['$scope', 'AlertSettings', 'Notifications', 'Analytics'];

    return AlertSettingController;
});
