define('App/Controllers/Trades/TradePerformanceController',['angular', 'underscore', 'highstock'], function (angular, _) {
    

    var controller = function ($scope, $http, _TRADE_MOREINFO_) {

        $scope.$on(_TRADE_MOREINFO_, function ($event, item) {
            var query = {
                '$expand': 'Trade_Performance',
                '$filter': 'trade_id eq ' + item.trade['trade_id']
            };
            item.isPerformanceLoaded = false;
            
            $http({
                method: 'GET',
                url: $scope.tradesUrl,
                params: query
            }).success(function (data) {

                if (data.value.length > 0) {

                    var yAxis = [];
                    var series = [];

                    var createSeries = function (p) {
                        var getColor = function (isAbs) {
                            return isAbs ? '#000000' : '#041f5b';
                        };

                        var getAxisName = function (isAbs) {
                            return isAbs ? 'absolute' : 'relative';
                        };

                        var getValueSuffix = function (isAbs, trade) {
                            var measure = trade[isAbs ? 'absolute_measure_type' : 'relative_measure_type'];
                            if (isAbs) {
                                return measure === 'Currency' ? trade['absolute_currency_code'] : (measure === 'Percent' ? '%' : measure);
                            } else {
                                return measure === 'Percent' ? '%' : measure;
                            }
                        };

                        series.push({
                            name: p.isAbsolute ? 'Absolute Performance' : 'Relative Performance',
                            color: getColor(p.isAbsolute),
                            yAxis: getAxisName(p.isAbsolute),
                            data: p.items,
                            tooltip: {
                                valueSuffix: getValueSuffix(p.isAbsolute, data.value[0])
                            }
                        });

                        yAxis.push({
                            id: getAxisName(p.isAbsolute),
                            labels: {
                                style: {
                                    color: getColor(p.isAbsolute)
                                }
                            },
                            opposite: p.isAbsolute
                        });
                        
                        if (p.isAbsolute) {
                            item.absoluteLabel = 'ABSOlUTE PERFORMANCE (' + getValueSuffix(p.isAbsolute, data.value[0]) + ')';
                        } else {
                            item.relativePerformanceLabel = 'RELATIVE PERFORMANCE';
                            item.versusLabel = 'VS ' + data.value[0]['benchmark_label'].toUpperCase() + ' (' + getValueSuffix(p.isAbsolute, data.value[0]) + ')';
                        }
                    };

                    _.chain(data.value[0]["Trade_Performance"])
                        .groupBy('return_benchmark_id')
                        .map(function (items, key) {
                            return {
                                isAbsolute: key == "null",
                                items: _.map(items, function (o) {
                                    return [new Date(o['return_date']).getTime(), Number(o['return_value'])];
                                })
                            };
                        })
                        .each(createSeries);

                    item.performanceChartConfig = {
                        chart: {
                            width: 1024
                        },
                        credits: {
                            enabled: false
                        },
                        exporting: {
                            enabled: false
                        },
                        rangeSelector: {
                            inputDateFormat: '%d %b %Y',
                            inputEditDateFormat: '%d %b %Y'
                        },
                        yAxis: yAxis,
                   
                        tooltip: {
                            shared: true
                        },
                        series: series,
                        isStockChart: true
                    };

                    item.isPerformanceEmpty = (series.length === 0);                    
                }
                item.isPerformanceLoaded = true;

            });
        });
    };

    controller.$inject = ['$scope', '$http', '_TRADE_MOREINFO_'];
    return controller;
});
define('App/Controllers/Trades/TradeRelatedViewsController',['angular', 'underscore'], function (angular, _) {
    

    var controller = function ($scope, DataEndpoint, $http, _TRADE_MOREINFO_, View) {
        $scope.relationType = 'views';
        $scope.parenthesis = function(str, n) {
            return str && str.length > n ? str.substring(0, n) + "..." : str;
        };
        $scope.$on(_TRADE_MOREINFO_, function ($event, item) {            
            if (item.relatedViews === undefined || item.relatedViews.length === 0) {
                DataEndpoint.getTemplatedEndpoint('trade-relations', [{ key: 'uri', value: item.trade.trade_uri }])
                    .then(function (url) {
                        $http.get(url).success(function (data) {
                            _.each(data['@graph'], function(views) {
                                if (views.informedByView !== undefined) {
                                    if (_.isArray(views.informedByView['@set'])) {
                                        item.relatedViews = views.informedByView['@set'];
                                    } else {
                                        item.relatedViews = [views.informedByView];
                                    }
                                }

                                var appendTheme = function(theme) {
                                    var found = _.find(item.relatedThemes, function (item) {
                                        return theme['@id'] === item['@id'];
                                    }) !== undefined;

                                    if (!found) {
                                        if (item.relatedThemes === undefined) {
                                            item.relatedThemes = [];
                                        }
                                        item.relatedThemes.push(theme);
                                    }
                                };

                                _.each(item.relatedViews, function (view) {
                                    view.positionValue = View.getPosition(view) + 1;
                                    view.convictionValue = View.getConviction(view);

                                    if (view.informedByTheme !== undefined) {
                                        if (_.isArray(view.informedByTheme['@set'])) {
                                            _.each(view.informedByTheme['@set'], appendTheme);
                                        } else {
                                            appendTheme(view.informedByTheme);
                                        }
                                    }
                                });
                            });
                        });
                    });
            }
        });
    };

    controller.$inject = ['$scope', 'DataEndpoint', '$http', '_TRADE_MOREINFO_', 'View'];
    return controller;
});
